        {reviews.map(review => (
            <div  >
                <ul className="second" >
                    <li key={review.id} className="ul" >{review.comment} <button type='delete' onClick={e => handleDelete(e)}> X </button></li>
                </ul>
            </div>
        ))}
    </div>
    )
}

export default Reviews
