import React, { useContext } from 'react'
import Popup from 'reactjs-popup'
import { AppContext } from '../services/app-context'

function LiquorCategory({props, addToCart}) {

  const { reviews } = useContext(AppContext);

  return (
    <div className='component liquors'>
        {
          props.map(obj => {
            const itemReviews = reviews.filter((rev) => rev['liquor_id'] === obj.id);

            return(

            <main id='liquor' key={obj.id}>
                <img src={obj.image_url} alt={obj.title} />
                <p className='liquor_details'> 
                  <b>{obj.title}</b>
                </p> <br /> &emsp;
                <Popup trigger={<button className='popup'> more details</button>} position="center center">
                  <img src={obj.image_url} alt={obj.title} />
                  <p> 
                    <b>{obj.title}</b><br /><br />
                    {obj.description} <br /><br />
                    Price : &nbsp; <span style={{color:'red', fontSize: 'large'}}> Ksh. {obj.price}</span> <br /> <br />
                  </p>
                  {reviews.filter(review => review.liquor_id === obj.id).maplength === 0 ? <p>There are no revies added yet</p> : <ul>
                    {itemReviews.map((item, index) => <li key={index}>{item.comment}</li>)}
                  </ul>}
                </Popup> &emsp; &emsp; &emsp; &emsp;
                <button 
                  className='click' 
                  onClick={e => addToCart(obj, 'increase')}>add to cart</button>
            </main>
            )}
          ) 
        }
    </div>
  )
}

export default LiquorCategory
